namespace CustomReports;

if ( ! defined( 'WPINC' ) ) {
	wp_die( 'No Access Allowed!', 'Error!', array( 'back_link' => true ) );
}

if ( ! class_exists( __NAMESPACE__ . '\\DashboardPage' ) ) {

	/**
	 * Dashboard Page.
	 *
	 * @author Jason Witt
	 * @since  1.0.0
	 */
	class DashboardPage {

		/**
		 * Initialize the class
		 *
		 * @author Jason Witt
		 * @since  1.0.0
		 *
		 * @return void
		 */
		public function __construct() {
			$this->hooks();
			$this->reports();
		}

		/**
		 * Init
		 *
		 * @author Jason Witt
		 * @since  1.0.0
		 *
		 * @return void
		 */
		public function reports() {
			new Favorites\Main();
			new Members\Main();
			new Pdfs\Main();
			new PdfBundles\Main();
		}

		/**
		 * Hooks
		 *
		 * @author Jason Witt
		 * @since  1.0.0
		 *
		 * @return void
		 */
		public function hooks() {
			add_action( 'admin_menu', array( $this, 'init' ) );
		}

		/**
		 * Init
		 *
		 * @author Jason Witt
		 * @since  1.0.0
		 *
		 * @return void
		 */
		public function init() {
			$icon = '';

			add_menu_page(
				__( 'Custom Reports', 'custom-reports' ),
				__( 'Custom Reports', 'custom-reports' ),
				'manage_options',
				'custom-reports',
				array( $this, 'render' ),
				'data:image/svg+xml;base64,' . $icon
			);
		}

		/**
		 * Render
		 *
		 * @author Jason Witt
		 * @since  1.0.0
		 *
		 * @return void
		 */
		public function render() {
			?>
			<div class="wrap">
				<h1>Reports</h1>
				<div class="custom-reports">
					<?php
						( new Favorites\Template() )->render();
						( new Members\Template() )->render();
						( new Pdfs\Template() )->render();
						( new PdfBundles\Template() )->render();
					?>
				</div>
			</div>
			<?php
		}
	}
}
